import * as substrate from "./substrate.mjs";
/** @internal */

export function getStorage(registry, metaVersion) {
  return {
    substrate: Object.entries(substrate).reduce((storage, [key, fn]) => {
      storage[key] = fn(registry, metaVersion);
      return storage;
    }, {})
  };
}